/******************************************************************
*   This is a customized parser which overrides the necessary     *
*   cases of Parser generated by SableCC. This parser constructs  *
*   a new simplified version of the AST while parsing.            *
*   The simplified grammer is introduced as ignored alternatives  *
*   in input grammer file to SableCC.                             *
******************************************************************/

// Modified by robby (robby@cis.ksu.edu)
// - New package names of the parser, lexer, abstract syntax
//   tree, and analysis
// - Add assertions

import edu.ksu.cis.bandera.jjjc.parser.*;
import edu.ksu.cis.bandera.jjjc.lexer.*;
import edu.ksu.cis.bandera.jjjc.node.*;
import edu.ksu.cis.bandera.jjjc.analysis.*;
import ca.mcgill.sable.util.*;

import java.util.Vector;
import java.util.Hashtable;
import java.util.Enumeration;
import java.util.BitSet;
import java.io.IOException;


public class JJCParser extends Parser
{
	ASTFixer fixer = new ASTFixer();
   public JJCParser(Lexer lexer)
	{
	super(lexer);
	}
	protected void filter()
	{
	node = fixer.fix(node);
	}
} // MyParser
